import React from "react";
import "./Card.css";
import PriorityIcon from "../assets/priority-icon.svg"; // Import priority icon
import TitleIcon from "../assets/title-icon.svg"; // Import title icon
import ThreeDotMenuIcon from "../assets/three-dot-menu.svg"; // Import 3-dot menu icon

const Card = ({ ticket, getUserName, sorting }) => {
  const getPriorityName = (priority) => {
    const priorities = ["No priority", "Low", "Medium", "High", "Urgent"];
    return priorities[priority];
  };

  const getSortingIcon = () => {
    if (sorting === "priority") {
      return <img src={PriorityIcon} alt="Priority Icon" className="icon" />;
    } else if (sorting === "title") {
      return <img src={TitleIcon} alt="Title Icon" className="icon" />;
    }
    return <img src={ThreeDotMenuIcon} alt="ThreeDotMenuIcon" className="icon" />;
  };

  return (
    <div className="card">
      <div className="card-header">
        <span className="card-id">{ticket.id}</span>
        <span className="card-avatar">
          {getUserName(ticket.userId).charAt(0)}
        </span>
      </div>
      <h3 className="card-title">
        {ticket.title}
      </h3>
      <div className="card-footer">
        <span className="card-priority">
          {getSortingIcon()} {getPriorityName(ticket.priority)}
        </span>
        {ticket.tag.map((tag, index) => (
          <span key={index} className="card-tag">
            {tag}
          </span>
        ))}
      </div>
    </div>
  );
};

export default Card;
